// Navbar.jsx
import React, { useState } from 'react';
import './Navbar.css';
import mentorImg from './mentor.png';
import Modal from './Modal';
import { useNavigate } from 'react-router-dom';

function Navbar() {
  const [activeProject, setActiveProject] = useState(null);
  const navigate = useNavigate();

  const openProject = (name) => {
    setActiveProject(name);
  };

  const closeModal = () => {
    setActiveProject(null);
  };

  const projects = [
    { name: 'Placement Predictor' },
    { name: 'Human Detection' },
  ];

  return (
    <div className="navbar-container">
      <div className="header" onClick={() => navigate('/')}>
        <img src={mentorImg} alt="Hanumat Lal Vishwakarma" className="mentor-img" />
        <div className="text-info">
          <h1 className="name">MY PORTFOLIO</h1>
          <p className="tagline">HANUMAT LAL VISHWAKARMA</p>
        </div>
      </div>

      <div className="project-links">
        {projects.map((project) => (
          <button
            key={project.name}
            className="project-link"
            onClick={() => openProject(project.name)}
          >
            {project.name}
          </button>
        ))}
      </div>

      <Modal isOpen={!!activeProject} onClose={closeModal}>
        {/* modal content comes from App.js route rendering */}
      </Modal>
    </div>
  );
}

export default Navbar;
